import{_ as s}from"./DefaultView.c3fa18c1.js";import{a,c as u,w as d,o as l,p as g,b as h,e,f as n}from"./entry.bbbe8ba6.js";const o={},i=r=>(g("data-v-ca9a73aa"),r=r(),h(),r),b=i(()=>e("head",null,[e("title",null,"KPJH - Datenschutz")],-1)),c=i(()=>e("div",{class:"body"},[e("h3",null,"1. Allgemeine Hinweise"),e("p",null,[n(" Die folgenden Hinweise geben einen einfachen Überblick darüber, was mit Deinen personenbezogenen Daten passiert, wenn Du diese Website besuchst oder mit der KjG Pfarrjugend Hüls oder dem Förderverein der Pfarrjugend Hüls e.V. kommunizierst. Personenbezogene Daten sind alle Daten, mit denen Du persönlich identifiziert werden kannst. Ausführliche Informationen zum Thema Datenschutz kannst Du unserer unter diesem Text aufgeführten Datenschutzerklärung entnehmen. "),e("br"),n(" Grundsätzlich erheben wir so wenig Daten von Dir wie möglich und werden Dich stets offen und transparent über alles informieren. Solltest Du einmal eine Frage haben oder etwas unklar sein, zögere bitte nicht, uns einfach anzusprechen. Diese Webseite wird gemeinsam von der KjG Pfarrjugend Hüls und dem Förderverein der Pfarrjugend Hüls e.V. betrieben. ")]),e("h3",null,"2. Datenerfassung bei der KjG Pfarrjugend Hüls"),e("p",null,[n(" Die KjG Pfarrjugend Hüls ist eine sogenannte Ortsgruppe des KjG Diözesanverbandes Aachen e.V. Die Katholischen Jungen Gemeinden (KjG) sind in den Diözesanen organisiert und gehören auf Bundesebene zur Bundesstelle der Katholischen jungen Gemeinde e.V. in Düsseldorf und sind somit auch unter dem Dachverband des Bund der Deutschen Katholischen Jugend (BDKJ) organisiert. Die KjG Pfarrjugend Hüls wurde in der Gemeinde St. Cyriakus gegründet und ist nach der Rechtsform ein nicht eingetragener Verein. Neben wöchentlichen Gruppenstunden finden regelmäßige Ausflüge sowie in den Sommerferien ein großes Zeltlager statt. Hier können grundsätzlich alle Mitglieder teilnehmen und in Einzelfällen sind auch Nicht-Mitglieder zu den Veranstaltungen zugelassen. Die Satzung der KjG Pfarrjugend Hüls kannst Du jederzeit auf kpjh.de nachlesen. "),e("br"),e("br"),n(" Es werden Daten von Mitgliedern, Leitern sowie Nicht-Mitgliedern gesammelt, um die Arbeit der KjG Pfarrjugend Hüls zu ermöglichen. Diese Daten sind z.B. Name, Adresse, Telefonnummer, E-Mail-Adresse und falls nötig auch personenbezogene Gesundheitsdaten um im Falle eines medizinischen Vorfalls schnellstmöglich reagieren zu können. Grundsätzlich verarbeitet die KjG Pfarrjugend Hüls Deine personenbezogenen Daten auf Grundlage von Art. 6 Abs. 1 lit. a DSGVO (aktive Einwilligung bzw. Art. 9 Abs. 2 lit. a DSGVO, sofern besondere Datenkategorien (z.B. Gesundheitsdaten) nach Art. 9 Abs. 1 DSGVO verarbeitet werden. Die Einwilligung ist jederzeit widerrufbar. Im Rahmen der Mitgliedsanträge oder für Nicht-Mitglieder bei Veranstaltungen werden Deine Daten auf Grundlage des Art. 6 Abs. 1 lit. b DSGVO. Des Weiteren verarbeiten wir Deine Daten, sofern diese zur Erfüllung einer rechtlichen Verpflichtung (z.B. Aufsichtspflichten oder Präventionsmaßnahmen) erforderlich sind auf Grundlage von Art. 6 Abs. 1 lit. c DSGVO. Die Datenverarbeitung kann ferner auf Grundlage unseres berechtigten Interesses nach Art. 6 Abs. 1 lit. f DSGVO erfolgen. "),e("br"),e("br"),n(" Über Details informieren wir Dich grundsätzlich auf den Anmeldebögen oder natürlich jederzeit auf Rückfrage. Du hast jederzeit das Recht, unentgeltlich Auskunft über Herkunft, Empfänger und Zweck Deiner gespeicherten personenbezogenen Daten zu erhalten. Du hast außerdem ein Recht, die Berichtigung oder Löschung dieser Daten zu verlangen. Wenn Du eine Einwilligung zur Datenverarbeitung erteilt hast, kannst Du diese Einwilligung jederzeit für die Zukunft widerrufen. Außerdem hast Du das Recht, unter bestimmten Umständen die Einschränkung der Verarbeitung Deiner personenbezogenen Daten zu verlangen. Des Weiteren steht Dir ein Beschwerderecht bei der zuständigen Aufsichtsbehörde zu. Weitere Details zu Deinen Rechten kannst Du dieser Datenschutzerklärung entnehmen. ")]),e("h3",null," 3. Datenerfassung bei dem Förderverein der Pfarrjugend Hüls e.V. "),e("p",null,[n(" Der Förderverein der Pfarrjugend Hüls e.V. wurde im Jahr 2023 von ehemaligen sowie aktiven Leitern der KjG Pfarrjugend Hüls gegründet. Der Zweck ist die Unterstützung der Jugendarbeit mit einem Fokus auf Krefeld-Hüls sowie speziell die Unterstützung der Arbeit der KjG Pfarrjugend Hüls. Jeder kann Fördermitglied werden und dadurch oder über einzelne Spenden die Aktivitäten des Fördervereins der Pfarrjugend Hüls e.V. unterstützen. Außerdem ist die Ausleihe von Zelten, Bierzeltgarnituren oder anderem Material sowie des 9-Sitzer Bus möglich. "),e("br"),e("br"),n(" Es werden Daten von Mitgliedern sowie Nicht-Mitgliedern gesammelt, um die Arbeit des Fördervereins der Pfarrjugend Hüls e.V. zu ermöglichen. Diese Daten sind z.B. Name, Adresse, Telefonnummer, E-Mail-Adresse und Kontodaten zur Abwicklung der Mitgliedsbeiträge sowie von Spenden. Grundsätzlich verarbeitet der Förderverein der Pfarrjugend Hüls e.V. "),e("br"),n(" Deine personenbezogenen Daten auf Grundlage von Art. 6 Abs. 1 lit. a DSGVO (aktive Einwilligung bzw. Art. 9 Abs. 2 lit. a DSGVO, sofern besondere Datenkategorien (z.B. Kontodaten) nach Art. 9 Abs. 1 DSGVO verarbeitet werden. Die Einwilligung ist jederzeit widerrufbar. Im Rahmen der Mitgliedsanträge oder für Nicht-Mitglieder bei Veranstaltungen werden Deine Daten auf Grundlage des Art. 6 Abs. 1 lit. b DSGVO. Des Weiteren verarbeiten wir Deine Daten, sofern diese zur Erfüllung einer rechtlichen Verpflichtung erforderlich sind auf Grundlage von Art. 6 Abs. 1 lit. c DSGVO (z.B. Spendenquittungen). Die Datenverarbeitung kann ferner auf Grundlage unseres berechtigten Interesses nach Art. 6 Abs. 1 lit. f DSGVO (z.B. Zeltverleih oder Busmiete) erfolgen. "),e("br"),e("br"),n(" Über Details informieren wir Dich grundsätzlich auf den Antragsbögen sowie Spendenformularen oder natürlich jederzeit auf Rückfrage. Du hast jederzeit das Recht, unentgeltlich Auskunft über Herkunft, Empfänger und Zweck Deiner gespeicherten personenbezogenen Daten zu erhalten. Du hast außerdem ein Recht, die Berichtigung oder Löschung dieser Daten zu verlangen. Wenn Du eine Einwilligung zur Datenverarbeitung erteilt hast, kannst Du diese Einwilligung jederzeit für die Zukunft widerrufen. Außerdem hast Du das Recht, unter bestimmten Umständen die Einschränkung der Verarbeitung Deiner personenbezogenen Daten zu verlangen. Des Weiteren steht Dir ein Beschwerderecht bei der zuständigen Aufsichtsbehörde zu. Weitere Details zu Deinen Rechten kannst Du dieser Datenschutzerklärung entnehmen. ")]),e("h3",null," 4. Allgemeine Hinweise zur Datenerfassung auf dieser Website "),e("b",null," 4.1. Wer ist verantwortlich für die Datenerfassung auf dieser Website? "),e("p",null," Die Datenverarbeitung auf dieser Website erfolgt durch den Websitebetreiber. Dessen Kontaktdaten kannst Du dem Abschnitt „Hinweis zur Verantwortlichen Stelle“ in dieser Datenschutzerklärung entnehmen. "),e("b",null," 4.2. Wie erfassen wir Deine Daten? "),e("p",null,[n(" Deine Daten werden zum einen dadurch erhoben, dass Du uns diese mitteilst. Hierbei kann es sich z. B. um Daten handeln, die Du in ein Kontaktformular eingibst. "),e("br"),n(" Andere Daten werden automatisch oder nach Deiner Einwilligung beim Besuch der Website durch unsere IT- Systeme erfasst. Das sind vor allem technische Daten (z. B. Internetbrowser, Betriebssystem oder Uhrzeit des Seitenaufrufs). Die Erfassung dieser Daten erfolgt automatisch, sobald Du diese Website betrittst. ")]),e("b",null," 4.3. Wofür nutzen wir Deine Daten? "),e("p",null," Ein Teil der Daten wird erhoben, um eine fehlerfreie Bereitstellung der Website zu gewährleisten. Andere Daten können zur Analyse Deines Nutzerverhaltens verwendet werden. "),e("b",null," 4.4. Welche Rechte hast Du bezüglich deiner Daten? "),e("p",null,[n(" Du hast jederzeit das Recht, unentgeltlich Auskunft über Herkunft, Empfänger und Zweck Deiner gespeicherten personenbezogenen Daten zu erhalten. Du hast außerdem ein Recht, die Berichtigung oder Löschung dieser Daten zu verlangen. Wenn Du eine Einwilligung zur Datenverarbeitung erteilt hast, kannst Du diese Einwilligung jederzeit für die Zukunft widerrufen. Außerdem hast Du das Recht, unter bestimmten Umständen die Einschränkung der Verarbeitung Deiner personenbezogenen Daten zu verlangen. Des Weiteren steht Dir ein Beschwerderecht bei der zuständigen Aufsichtsbehörde zu. "),e("br"),n(" Hierzu sowie zu weiteren Fragen zum Thema Datenschutz kannst Du dich jederzeit an uns wenden. ")]),e("h3",null,"5. Hosting"),e("p",null,[n(" Wir hosten die Inhalte unserer Website bei folgendem Anbieter: "),e("br"),e("strong",null,"DomainFactory"),n(),e("br"),n(" Anbieter ist die DomainFactory GmbH, c/o WeWork, Neuturmstraße 5, 80331 München (nachfolgend DomainFactory). Wenn Du unsere Website besuchst, erfasst DomainFactory verschiedene Logfiles inklusive Deiner IP-Adressen. "),e("br"),n(" Details entnimmst Du bitte der Datenschutzerklärung von DomainFactory: "),e("a",{href:"https://www.df.eu/de/datenschutz/"},"https://www.df.eu/de/datenschutz/"),n(". "),e("br"),e("br"),n(" Die Verwendung von DomainFactory erfolgt auf Grundlage von Art. 6 Abs. 1 lit. f DSGVO. Wir haben ein berechtigtes Interesse an einer möglichst zuverlässigen Darstellung unserer Website. Sofern eine entsprechende Einwilligung abgefragt wurde, erfolgt die Verarbeitung ausschließlich auf Grundlage von Art. 6 Abs. 1 lit. a DSGVO und § 25 Abs. 1 TTDSG, soweit die Einwilligung die Speicherung von Cookies oder den Zugriff auf Informationen im Endgerät des Nutzers (z. B. für Device-Fingerprinting) im Sinne des TTDSG umfasst. Die Einwilligung ist jederzeit widerrufbar. ")]),e("b",null," Auftragsverarbeitung "),e("p",null," Wir haben einen Vertrag über Auftragsverarbeitung (AVV) zur Nutzung des oben genannten Dienstes geschlossen. Hierbei handelt es sich um einen datenschutzrechtlich vorgeschriebenen Vertrag, der gewährleistet, dass dieser die personenbezogenen Daten unserer Websitebesucher nur nach unseren Weisungen und unter Einhaltung der DSGVO verarbeitet. "),e("h3",null,"6. Allgemeine Hinweise und Pflichtinformationen"),e("b",null," 6.1. Datenschutz "),e("p",null,[n(" Die Betreiber dieser Seiten nehmen den Schutz Deiner persönlichen Daten sehr ernst. Wir behandeln Deine personenbezogenen Daten vertraulich und entsprechend den gesetzlichen Datenschutzvorschriften sowie dieser Datenschutzerklärung. "),e("br"),n(" Wenn Du diese Website benutzt, werden verschiedene personenbezogene Daten erhoben. Personenbezogene Daten sind Daten, mit denen Du persönlich identifiziert werden kannst. Die vorliegende Datenschutzerklärung erläutert, welche Daten wir erheben und wofür wir sie nutzen. Sie erläutert auch, wie und zu welchem Zweck das geschieht. "),e("br"),n(" Wir weisen darauf hin, dass die Datenübertragung im Internet (z. B. bei der Kommunikation per E-Mail) Sicherheitslücken aufweisen kann. Ein lückenloser Schutz der Daten vor dem Zugriff durch Dritte ist nicht möglich. ")]),e("b",null," 6.2. Hinweis zur verantwortlichen Stelle "),e("p",null,[n(" Die verantwortliche Stelle für die Datenverarbeitung auf dieser Website ist: "),e("br"),n(" Förderverein der Pfarrjugend Hüls e.V. "),e("br"),n(" Christian Paffhausen (1. Vorsitzender) "),e("br"),n(" Dünkirchener Straße 19 "),e("br"),n(" 47839 Krefeld "),e("br"),n(" Telefon: 015736575973 (Bitte nur in dringenden Fällen verwenden) "),e("br"),n(" E-Mail: "),e("a",{href:"mailto:foerderverein@kpjh.de"},"foerderverein@kpjh.de"),e("br"),e("br"),n(" Verantwortliche Stelle ist die natürliche oder juristische Person, die allein oder gemeinsam mit anderen über die Zwecke und Mittel der Verarbeitung von personenbezogenen Daten (z. B. Namen, E-Mail-Adressen o. Ä.) entscheidet. ")]),e("b",null," 6.3. Speicherdauer "),e("p",null," Soweit innerhalb dieser Datenschutzerklärung keine speziellere Speicherdauer genannt wurde, verbleiben Deine personenbezogenen Daten bei uns, bis der Zweck für die Datenverarbeitung entfällt. Wenn Du ein berechtigtes Löschersuchen geltend machen oder eine Einwilligung zur Datenverarbeitung widerrufen möchtest, werden Deine Daten gelöscht, sofern wir keine anderen rechtlich zulässigen Gründe für die Speicherung Deine personenbezogenen Daten haben (z. B. steuer- oder handelsrechtliche Aufbewahrungsfristen); im letztgenannten Fall erfolgt die Löschung nach Fortfall dieser Gründe. "),e("b",null," 6.4. Allgemeine Hinweise zu den Rechtsgrundlagen der Datenverarbeitung auf dieser Website "),e("p",null," Sofern Du in die Datenverarbeitung eingewilligt hast, verarbeiten wir Deine personenbezogenen Daten auf Grundlage von Art. 6 Abs. 1 lit. a DSGVO bzw. Art. 9 Abs. 2 lit. a DSGVO, sofern besondere Datenkategorien nach Art. 9 Abs. 1 DSGVO verarbeitet werden. Im Falle einer ausdrücklichen Einwilligung in die Übertragung personenbezogener Daten in Drittstaaten erfolgt die Datenverarbeitung außerdem auf Grundlage von Art. 49 Abs. 1 lit. a DSGVO. Sofern Du in die Speicherung von Cookies oder in den Zugriff auf Informationen in Dein Endgerät (z. B. via Device-Fingerprinting) eingewilligt hast, erfolgt die Datenverarbeitung zusätzlich auf Grundlage von § 25 Abs. 1 TTDSG. Die Einwilligung ist jederzeit widerrufbar. Sind Deine Daten zur Vertragserfüllung oder zur Durchführung vorvertraglicher Maßnahmen erforderlich, verarbeiten wir Deine Daten auf Grundlage des Art. 6 Abs. 1 lit. b DSGVO. Des Weiteren verarbeiten wir Deine Daten, sofern diese zur Erfüllung einer rechtlichen Verpflichtung erforderlich sind auf Grundlage von Art. 6 Abs. 1 lit. c DSGVO. Die Datenverarbeitung kann ferner auf Grundlage unseres berechtigten Interesses nach Art. 6 Abs. 1 lit. f DSGVO erfolgen. Über die jeweils im Einzelfall einschlägigen Rechtsgrundlagen wird in den folgenden Absätzen dieser Datenschutzerklärung informiert. "),e("b",null," 6.5. Empfänger von personenbezogenen Daten "),e("p",null," Im Rahmen unserer Geschäftstätigkeit arbeiten wir mit verschiedenen externen Stellen zusammen. Dabei ist teilweise auch eine Übermittlung von personenbezogenen Daten an diese externen Stellen erforderlich. Wir geben personenbezogene Daten nur dann an externe Stellen weiter, wenn dies im Rahmen einer Vertragserfüllung erforderlich ist, wenn wir gesetzlich hierzu verpflichtet sind (z. B. Weitergabe von Daten an Steuerbehörden), wenn wir ein berechtigtes Interesse nach Art. 6 Abs. 1 lit. f DSGVO an der Weitergabe haben oder wenn eine sonstige Rechtsgrundlage die Datenweitergabe erlaubt. Beim Einsatz von Auftragsverarbeitern geben wir personenbezogene Daten unserer Kunden bzw. Mitglieder nur auf Grundlage eines gültigen Vertrags über Auftragsverarbeitung weiter. Im Falle einer gemeinsamen Verarbeitung wird ein Vertrag über gemeinsame Verarbeitung geschlossen. "),e("b",null," 6.6. Widerruf Ihrer Einwilligung zur Datenverarbeitung "),e("p",null," Viele Datenverarbeitungsvorgänge sind nur mit Deiner ausdrücklichen Einwilligung möglich. Du kannst eine bereits erteilte Einwilligung jederzeit widerrufen. Die Rechtmäßigkeit der bis zum Widerruf erfolgten Datenverarbeitung bleibt vom Widerruf unberührt. "),e("b",null," 6.7. SSL- bzw. TLS-Verschlüsselung "),e("p",null,[n(" Diese Seite nutzt aus Sicherheitsgründen und zum Schutz der Übertragung vertraulicher Inhalte, wie zum Beispiel Bestellungen oder Anfragen, die Du an uns als Seitenbetreiber sendest, eine SSL- bzw. TLS- Verschlüsselung. Eine verschlüsselte Verbindung erkennst Du daran, dass die Adresszeile des Browsers von „http://“ auf „https://“ wechselt und an dem Schloss-Symbol in Deiner Browserzeile. "),e("br")]),e("h3",null," 7. Rechte und Pflichten zur Datenerhebung sowie Datenverarbeitung "),e("b",null," 7.1. Widerspruchsrecht gegen die Datenerhebung in besonderen Fällen sowie gegen Direktwerbung (Art. 21 DSGVO) "),e("p",null,[n(" Wenn die Datenverarbeitung auf Grundlage von Art. 6 Abs. 1 lit. e oder f DSGVO erfolgt, hast Du jederzeit das Recht, aus Gründen, die sich aus Deiner besonderen Situation ergeben, gegen die Verarbeitung Deiner personenbezogenen Daten Widerspruch einzulegen; dies gilt auch für ein auf diese Bestimmungen gestütztes Profiling. Die jeweilige Rechtsgrundlage, auf denen eine Verarbeitung beruht, entnimmst Du dieser Datenschutzerklärung. Wenn Du Widerspruch einlegst, werden wir Deine betroffenen personenbezogenen Daten nicht mehr verarbeiten, es sei denn, wir können zwingende schutzwürdige Gründe für die Verarbeitung nachweisen, die Deinen Interessen, Rechte und Freiheiten überwiegen oder die Verarbeitung dient der Geltendmachung, Ausübung oder Verteidigung von Rechtsansprüchen (Widerspruch nach Art. 21 Abs. 1 DSGVO). "),e("br"),e("br"),n(" Werden Deine personenbezogenen Daten verarbeitet, um Direktwerbung zu betreiben, so hast Du das Recht, jederzeit Widerspruch gegen die Verarbeitung Dich betreffender personenbezogener Daten zum Zwecke derartiger Werbung einzulegen; dies gilt auch für das Profiling, soweit es mit solcher Direktwerbung in Verbindung steht. Wenn Du widersprichst, werden Deine personenbezogenen Daten anschließend nicht mehr zum Zwecke der Direktwerbung verwendet (Widerspruch nach Art. 21 Abs. 2 DSGVO). ")]),e("b",null," 7.2. Beschwerderecht bei der zuständigen Aufsichtsbehörde "),e("p",null," Im Falle von Verstößen gegen die DSGVO steht den Betroffenen ein Beschwerderecht bei einer Aufsichtsbehörde, insbesondere in dem Mitgliedstaat ihres gewöhnlichen Aufenthalts, ihres Arbeitsplatzes oder des Orts des mutmaßlichen Verstoßes zu. Das Beschwerderecht besteht unbeschadet anderweitiger verwaltungsrechtlicher oder gerichtlicher Rechtsbehelfe. "),e("b",null," 7.3. Recht auf Datenübertragbarkeit "),e("p",null," Du hast das Recht, Daten, die wir auf Grundlage Deiner Einwilligung oder in Erfüllung eines Vertrags automatisiert verarbeiten, an Dich oder an einen Dritten in einem gängigen, maschinenlesbaren Format aushändigen zu lassen. Sofern Du die direkte Übertragung der Daten an einen anderen Verantwortlichen verlangst, erfolgt dies nur, soweit es technisch machbar ist. "),e("b",null," 7.4. Auskunft, Berichtigung und Löschung "),e("p",null," Du hast im Rahmen der geltenden gesetzlichen Bestimmungen jederzeit das Recht auf unentgeltliche Auskunft über Deine gespeicherten personenbezogenen Daten, deren Herkunft und Empfänger und den Zweck der Datenverarbeitung und ggf. ein Recht auf Berichtigung oder Löschung dieser Daten. Hierzu sowie zu weiteren Fragen zum Thema personenbezogene Daten kannst Du dich jederzeit an uns wenden. "),e("b",null," 7.5. Recht auf Einschränkung der Verarbeitung "),e("p",null,[n(" Du hast das Recht, die Einschränkung der Verarbeitung Ihrer personenbezogenen Daten zu verlangen. Hierzu kannst Du dich jederzeit an uns wenden. Das Recht auf Einschränkung der Verarbeitung besteht in folgenden Fällen: "),e("br"),n(" Wenn Du die Richtigkeit Deiner bei uns gespeicherten personenbezogenen Daten bestreitest, benötigen wir in der Regel Zeit, um dies zu überprüfen. Für die Dauer der Prüfung hast Du das Recht, die Einschränkung der Verarbeitung Deiner personenbezogenen Daten zu verlangen. "),e("br"),n(" Wenn die Verarbeitung Deiner personenbezogenen Daten unrechtmäßig geschah/geschieht, kannst Du statt der Löschung die Einschränkung der Datenverarbeitung verlangen. "),e("br"),n(" Wenn wir Deine personenbezogenen Daten nicht mehr benötigen, Du sie jedoch zur Ausübung, Verteidigung oder Geltendmachung von Rechtsansprüchen benötigst, hast Du das Recht, statt der Löschung die Einschränkung der Verarbeitung Deiner personenbezogenen Daten zu verlangen. "),e("br"),n(" Wenn Du einen Widerspruch nach Art. 21 Abs. 1 DSGVO eingelegt hast, muss eine Abwägung zwischen Deinen und unseren Interessen vorgenommen werden. Solange noch nicht feststeht, wessen Interessen überwiegen, hast Du das Recht, die Einschränkung der Verarbeitung Deiner personenbezogenen Daten zu verlangen. "),e("br"),e("br"),n(" Wenn Du die Verarbeitung Ihrer personenbezogenen Daten eingeschränkt hast, dürfen diese Daten – von ihrer Speicherung abgesehen – nur mit Deiner Einwilligung oder zur Geltendmachung, Ausübung oder Verteidigung von Rechtsansprüchen oder zum Schutz der Rechte einer anderen natürlichen oder juristischen Person oder aus Gründen eines wichtigen öffentlichen Interesses der Europäischen Union oder eines Mitgliedstaats verarbeitet werden. ")]),e("h3",null," 8. Detaillierte Informationen zur Datenerfassung auf dieser Website "),e("b",null," 8.1. Kontaktformular "),e("p",null,[n(" Wenn Du uns per Kontaktformular Anfragen zukommen lässt, werden Deine Angaben aus dem Anfrageformular inklusive der von Dir dort angegebenen Kontaktdaten zwecks Bearbeitung der Anfrage und für den Fall von Anschlussfragen bei uns gespeichert. Diese Daten geben wir nicht ohne Deine Einwilligung weiter. "),e("br"),e("br"),n(" Die Verarbeitung dieser Daten erfolgt auf Grundlage von Art. 6 Abs. 1 lit. b DSGVO, sofern Deine Anfrage mit der Erfüllung eines Vertrags zusammenhängt oder zur Durchführung vorvertraglicher Maßnahmen erforderlich ist. In allen übrigen Fällen beruht die Verarbeitung auf unserem berechtigten Interesse an der effektiven Bearbeitung der an uns gerichteten Anfragen (Art. 6 Abs. 1 lit. f DSGVO) oder auf Ihrer Einwilligung (Art. 6 Abs. 1 lit. a DSGVO) sofern diese abgefragt wurde; die Einwilligung ist jederzeit widerrufbar. "),e("br"),e("br"),n(" Die von Dir im Kontaktformular eingegebenen Daten verbleiben bei uns, bis Du uns zur Löschung aufforderst, Deine Einwilligung zur Speicherung widerrufst oder der Zweck für die Datenspeicherung entfällt (z. B. nach abgeschlossener Bearbeitung Deiner Anfrage). Zwingende gesetzliche Bestimmungen – insbesondere Aufbewahrungsfristen – bleiben unberührt. ")]),e("b",null," 8.2. Anfrage per E-Mail, Telefon oder Telefax "),e("p",null,[n(" Wenn Du uns per E-Mail, Telefon oder Telefax kontaktierst, wird Deine Anfrage inklusive aller daraus hervorgehenden personenbezogenen Daten (Name, Anfrage) zum Zwecke der Bearbeitung Deines Anliegens bei uns gespeichert und verarbeitet. Diese Daten geben wir nicht ohne Deine Einwilligung weiter. "),e("br"),e("br"),n(" Die Verarbeitung dieser Daten erfolgt auf Grundlage von Art. 6 Abs. 1 lit. b DSGVO, sofern Deine Anfrage mit der Erfüllung eines Vertrags zusammenhängt oder zur Durchführung vorvertraglicher Maßnahmen erforderlich ist. In allen übrigen Fällen beruht die Verarbeitung auf unserem berechtigten Interesse an der effektiven Bearbeitung der an uns gerichteten Anfragen (Art. 6 Abs. 1 lit. f DSGVO) oder auf Deiner Einwilligung (Art. 6 Abs. 1 lit. a DSGVO) sofern diese abgefragt wurde; die Einwilligung ist jederzeit widerrufbar. "),e("br"),e("br"),n(" Die von Dir an uns per Kontaktanfragen übersandten Daten verbleiben bei uns, bis Du uns zur Löschung aufforderst, Deine Einwilligung zur Speicherung widerrufst oder der Zweck für die Datenspeicherung entfällt (z. B. nach abgeschlossener Bearbeitung Ihres Anliegens). Zwingende gesetzliche Bestimmungen – insbesondere gesetzliche Aufbewahrungsfristen – bleiben unberührt. ")]),e("b",null," 8.3. Newsletter "),e("p",null,[n(" Wenn Du den auf der Website angebotenen Newsletter beziehen möchtest, benötigen wir von Dir eine E- Mail-Adresse sowie Informationen, welche uns die Überprüfung gestatten, dass Du der Inhaber der angegebenen E-Mail-Adresse bist und mit dem Empfang des Newsletters einverstanden sind. Weitere Daten werden nicht bzw. nur auf freiwilliger Basis erhoben. Diese Daten verwenden wir ausschließlich für den Versand der angeforderten Informationen und geben diese nicht an Dritte weiter. "),e("br"),n(" Die Verarbeitung der in das Newsletteranmeldeformular eingegebenen Daten erfolgt ausschließlich auf Grundlage Deiner Einwilligung (Art. 6 Abs. 1 lit. a DSGVO). Die erteilte Einwilligung zur Speicherung der Daten, der E-Mail-Adresse sowie deren Nutzung zum Versand des Newsletters kannst Du jederzeit widerrufen, etwa über den „Austragen“-Link im Newsletter. Die Rechtmäßigkeit der bereits erfolgten Datenverarbeitungsvorgänge bleibt vom Widerruf unberührt. "),e("br"),e("br"),n(" Die von Dir zum Zwecke des Newsletter-Bezugs bei uns hinterlegten Daten werden von uns bis zu Deiner Austragung aus dem Newsletter bei uns bzw. dem Newsletterdiensteanbieter gespeichert und nach der Abbestellung des Newsletters oder nach Zweckfortfall aus der Newsletterverteilerliste gelöscht. Wir behalten uns vor, E-Mail-Adressen aus unserem Newsletterverteiler nach eigenem Ermessen im Rahmen unseres berechtigten Interesses nach Art. 6 Abs. 1 lit. f DSGVO zu löschen oder zu sperren. Daten, die zu anderen Zwecken bei uns gespeichert wurden, bleiben hiervon unberührt. "),e("br"),n(" Nach Deiner Austragung aus der Newsletterverteilerliste wird Deiner E-Mail-Adresse bei uns bzw. dem Newsletterdiensteanbieter ggf. in einer Blacklist gespeichert, sofern dies zur Verhinderung künftiger Mailings erforderlich ist. Die Daten aus der Blacklist werden nur für diesen Zweck verwendet und nicht mit anderen Daten zusammengeführt. Dies dient sowohl Deinem Interesse als auch unserem Interesse an der Einhaltung der gesetzlichen Vorgaben beim Versand von Newslettern (berechtigtes Interesse im Sinne des Art. 6 Abs. 1 lit. f DSGVO). Die Speicherung in der Blacklist ist zeitlich nicht befristet. Du kannst der Speicherung widersprechen, sofern Deine Interessen unser berechtigtes Interesse überwiegen. ")]),e("h3",null,"9. Plugins und Tools"),e("b",null," 9.1. YouTube mit erweitertem Datenschutz "),e("p",null,[n(" Diese Website bindet Videos der Website YouTube ein. Betreiber der Seiten ist die Google Ireland Limited („Google“), Gordon House, Barrow Street, Dublin 4, Irland. "),e("br"),n(" Wir nutzen YouTube im erweiterten Datenschutzmodus. Dieser Modus bewirkt laut YouTube, dass YouTube keine Informationen über die Besucher auf dieser Website speichert, bevor diese sich das Video ansehen. Die Weitergabe von Daten an YouTube-Partner wird durch den erweiterten Datenschutzmodus hingegen nicht zwingend ausgeschlossen. So stellt YouTube – unabhängig davon, ob Du Dir ein Video ansiehst – eine Verbindung zum Google Marketing Netzwerk her. "),e("br"),n(" Sobald Du ein YouTube-Video auf dieser Website startest, wird eine Verbindung zu den Servern von YouTube hergestellt. Dabei wird dem YouTube-Server mitgeteilt, welche unserer Seiten Du besucht hast. Wenn Du in Deinem YouTube-Account eingeloggt bist, ermöglichst Du YouTube, Dein Surfverhalten direkt Deinem persönlichen Profil zuzuordnen. Dies kannst Du verhindern, indem Du Dich aus Deinem YouTube- Account ausloggen. "),e("br"),n(" Des Weiteren kann YouTube nach Starten eines Videos verschiedene Cookies auf Deinem Endgerät speichern oder vergleichbare Wiedererkennungstechnologien (z. B. Device-Fingerprinting) einsetzen. Auf diese Weise kann YouTube Informationen über Besucher dieser Website erhalten. Diese Informationen werden u. a. verwendet, um Videostatistiken zu erfassen, die Anwenderfreundlichkeit zu verbessern und Betrugsversuchen vorzubeugen. "),e("br"),n(" Gegebenenfalls können nach dem Start eines YouTube-Videos weitere Datenverarbeitungsvorgänge ausgelöst werden, auf die wir keinen Einfluss haben. "),e("br"),n(" Die Nutzung von YouTube erfolgt im Interesse einer ansprechenden Darstellung unserer Online-Angebote. Dies stellt ein berechtigtes Interesse im Sinne von Art. 6 Abs. 1 lit. f DSGVO dar. Sofern eine entsprechende Einwilligung abgefragt wurde, erfolgt die Verarbeitung ausschließlich auf Grundlage von Art. 6 Abs. 1 lit. a DSGVO und § 25 Abs. 1 TTDSG, soweit die Einwilligung die Speicherung von Cookies oder den Zugriff auf Informationen im Endgerät des Nutzers (z. B. Device-Fingerprinting) im Sinne des TTDSG umfasst. Die Einwilligung ist jederzeit widerrufbar. "),e("br"),n(" Weitere Informationen über Datenschutz bei YouTube findest Du in deren Datenschutzerklärung unter: "),e("a",{href:"https://policies.google.com/privacy?hl=de"},"https://policies.google.com/privacy?hl=de"),n(". "),e("br"),e("br"),n(" Das Unternehmen verfügt über eine Zertifizierung nach dem „EU-US Data Privacy Framework“ (DPF). Der DPF ist ein Übereinkommen zwischen der Europäischen Union und den USA, der die Einhaltung europäischer Datenschutzstandards bei Datenverarbeitungen in den USA gewährleisten soll. Jedes nach dem DPF zertifizierte Unternehmen verpflichtet sich, diese Datenschutzstandards einzuhalten. Weitere Informationen hierzu erhältst Du vom Anbieter unter folgendem Link: "),e("a",{href:"https://www.dataprivacyframework.gov/s/participant-search/participant- detail?contact=true&id=a2zt000000001L5AAI&status=Active"},"https://www.dataprivacyframework.gov/s/participant-search/participant- detail?contact=true&id=a2zt000000001L5AAI&status=Active")]),e("b",null," 9.2. Google Fonts (lokales Hosting) "),e("p",null,[n(" Diese Seite nutzt zur einheitlichen Darstellung von Schriftarten so genannte Google Fonts, die von Google bereitgestellt werden. Die Google Fonts sind lokal installiert. Eine Verbindung zu Servern von Google findet dabei nicht statt. "),e("br"),n(" Weitere Informationen zu Google Fonts findest Du unter "),e("a",{href:"https://developers.google.com/fonts/faq"},"https://developers.google.com/fonts/faq"),n(" und in der Datenschutzerklärung von Google: "),e("a",{href:"https://policies.google.com/privacy?hl=de"},"https://policies.google.com/privacy?hl=de"),n(". ")]),e("b",null," 9.3. Google Drive "),e("p",null,[n(" Wir haben Google Drive auf dieser Website eingebunden. Anbieter ist die Google Ireland Limited („Google“), Gordon House, Barrow Street, Dublin 4, Irland. "),e("br"),n(" Google Drive ermöglicht es uns, einen Uploadbereich auf unserer Website einzubinden, in dem wir Inhalte hochladen können. Wenn Du Inhalte hochlädst oder diese anzeigst, werden diese auf den Servern von Google Drive gespeichert bzw. abgerufen. Wenn Du unsere Website betritst, wird außerdem eine Verbindung zu Google Drive aufgebaut, sodass Google Drive feststellen kann, dass Du unsere Website besucht hast. "),e("br"),n(" Die Verwendung von Google Drive erfolgt auf Grundlage von Art. 6 Abs. 1 lit. f DSGVO. Der Websitebetreiber hat ein berechtigtes Interesse an einem zuverlässigen Uploadbereich auf seiner Website. Sofern eine entsprechende Einwilligung abgefragt wurde, erfolgt die Verarbeitung ausschließlich auf Grundlage von Art. 6 Abs. 1 lit. a DSGVO; die Einwilligung ist jederzeit widerrufbar. "),e("br"),e("br"),n(" Das Unternehmen verfügt über eine Zertifizierung nach dem „EU-US Data Privacy Framework“ (DPF). Der DPF ist ein Übereinkommen zwischen der Europäischen Union und den USA, der die Einhaltung europäischer Datenschutzstandards bei Datenverarbeitungen in den USA gewährleisten soll. Jedes nach dem DPF zertifizierte Unternehmen verpflichtet sich, diese Datenschutzstandards einzuhalten. Weitere Informationen hierzu erhalten Sie vom Anbieter unter folgendem Link: "),e("a",{href:"https://www.dataprivacyframework.gov/s/participant-search/participant- detail?contact=true&id=a2zt000000001L5AAI&status=Active"},"https://www.dataprivacyframework.gov/s/participant-search/participant- detail?contact=true&id=a2zt000000001L5AAI&status=Active")])],-1));function D(r,f){const t=s;return l(),u(t,{titel:"Unsere Datenschutzerklärung"},{default:d(()=>[b,c]),_:1})}const z=a(o,[["render",D],["__scopeId","data-v-ca9a73aa"]]);export{z as default};
